CREATE TABLE `pl_class` (
  `classid` int(11) NOT NULL COMMENT '分类ID',
  `classname` varchar(255) DEFAULT NULL,
  `classcontents` text COMMENT '分类内容，用于栏目系统等',
  `imgurl` varchar(500) DEFAULT NULL COMMENT '分类图片',
  `parentid` int(11) DEFAULT '0' COMMENT '父分类ID',
  `depth` int(11) DEFAULT '1' COMMENT '深度',
  `isout` tinyint(2) DEFAULT '0' COMMENT '是否外部链接',
  `outurl` varchar(500) DEFAULT NULL COMMENT '链接地址',
  `visible` tinyint(2) DEFAULT '0' COMMENT '是否隐藏',
  `orderby` int(11) DEFAULT '0',
  `childid` text COMMENT '分类孩子数',
  `seo_title` varchar(500) DEFAULT NULL COMMENT 'SEO标题',
  `seo_keyword` varchar(500) DEFAULT NULL COMMENT 'SEO关键词',
  `seo_description` varchar(500) DEFAULT NULL COMMENT 'SEO描述'
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

