CREATE TABLE `pl_config` (
  `id` int(11) NOT NULL,
  `webname` varchar(255) DEFAULT NULL COMMENT '网站名称',
  `weburl` varchar(255) DEFAULT NULL COMMENT '网站地址',
  `webdir` varchar(255) DEFAULT NULL COMMENT '网站安装目录',
  `weblogo` varchar(500) DEFAULT NULL COMMENT '网站logo图片',
  `webbanner` varchar(500) DEFAULT NULL COMMENT '网站Banner',
  `webseotitle` varchar(500) DEFAULT NULL COMMENT '网站标题',
  `index_seotitle` varchar(500) DEFAULT NULL COMMENT '首页SEO标题',
  `index_seokeywords` varchar(500) DEFAULT NULL COMMENT '网站关键词',
  `index_seodescription` varchar(500) DEFAULT NULL COMMENT '网站描述',
  `iswatermark` tinyint(4) DEFAULT '0' COMMENT '是否水印，0为不水印，1为文字水印，2为图片水印',
  `watermark_text` varchar(255) DEFAULT NULL COMMENT '水印文字',
  `watermark_text_color` varchar(10) DEFAULT NULL COMMENT '水印文字颜色',
  `watermark_text_size` int(3) DEFAULT NULL COMMENT '水印文字大小',
  `watermark_img` varchar(500) DEFAULT NULL COMMENT '水印图片',
  `iswebclose` tinyint(4) DEFAULT '0' COMMENT '是否关闭网站，1为关闭。',
  `webclose_cause` varchar(1000) DEFAULT NULL COMMENT '网站关闭原因'
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

