  `fbcontent` text COMMENT '留言内容',
  `author` varchar(255) DEFAULT NULL COMMENT '留言者',
  `updatetime` datetime DEFAULT NULL,
  `userid` int(11) DEFAULT '0' COMMENT '会员ID，备用',
  `myphone` varchar(255) DEFAULT NULL COMMENT '电话',
  `email` varchar(255) DEFAULT NULL,
  `qq` varchar(255) DEFAULT NULL,
  `address` varchar(500) DEFAULT NULL COMMENT '地址',
  `replycontent` text COMMENT '回复内容',
  `replytime` datetime DEFAULT NULL COMMENT '回复时间',
  `replyman` varchar(255) DEFAULT NULL COMMENT '回复人',
  `tuijian` tinyint(4) DEFAULT '0' COMMENT '是否推荐，0不推，1推',
  `shenhe` tinyint(4) DEFAULT '1' COMMENT '是否审核，0未申，1已审，2不通过',
  `hits` int(11) DEFAULT '0' COMMENT '点击数',
  `orderid` int(11) DEFAULT '0' COMMENT '排序'
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

