  `hr_experience` varchar(255) DEFAULT NULL COMMENT '工作经验',
  `hr_sex` varchar(255) DEFAULT NULL COMMENT '性别要求',
  `hr_stature` varchar(255) DEFAULT NULL COMMENT '身高要求',
  `hr_salary` varchar(255) DEFAULT NULL COMMENT '薪水待遇',
  `hr_address` varchar(255) DEFAULT NULL COMMENT '工作地点',
  `hr_useful_life` datetime DEFAULT NULL COMMENT '职位有效期',
  `hr_content` text COMMENT '招聘详情',
  `hits` int(11) DEFAULT '0' COMMENT '点击数',
  `updatetime` datetime DEFAULT NULL COMMENT '更新时间',
  `tuijian` tinyint(4) DEFAULT '0' COMMENT '是否推荐，0不推，1推',
  `shenhe` tinyint(4) DEFAULT '1' COMMENT '是否审核，0为未审，1为已审，2为审核不通过',
