INSERT INTO `pl_label` (`lbid`, `classid`, `lbtitle`, `lbmarker`, `lbcontent`, `updatetime`, `orderid`) VALUES
(1, 9, '网站底部', 'webfoot', '<p>办公地址:广西贵港市港北区金港大道XXX号 工厂地址:贵港市覃塘区XXX工业区 </p><p> 联系电话:0775-4500001 0775-4500002 0775-4500003 传真号码:0775-4500000</p><p> 手机号码:151xxxxx100 134xxxxx228 邮箱:dzh188@qq.com 网址:www.caozha.com </p><p>版权所有：贵港市XX家具有限公司 &nbsp;<a href=\"http://caozha.com/\" target=\"_blank\" title=\"源码开发，网站建设，域名注册，网站空间，网站推广。\" textvalue=\"技术支持：邓草\">技术支持：邓草</a></p>', '2012-08-08 15:51:51', 0),
(2, 9, '首页公司简介', 'about_us', '<p>　　贵港市XX家具有限公司是一家集研发、设计、生产、销售于一体的专业性办公家具公司。公司自成立以来，始终坚持以人为本的原则，注重产品的优良品质，全力打造办公家具的优秀品牌。 &nbsp;</p><p>　　公司实力雄厚，经过多年的经营，我们公司逐步形成多样化、专业化、规模化的经营战略.引进先进的生产设备及制造工艺，产品有大班台、屏风卡座、职员桌、会议桌、办公沙发、转椅、影剧院椅、宾馆客房家具等几个大系列产品，公司更推出设计、运输、安装、维修、保养一条龙服务，并在同行业中率先做出&quot;产品质量三年三包，五年免费保修&quot;的承诺。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>', '2012-08-08 16:01:25', 0),
(3, 9, '首页联系我们', 'index_contact', '<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" align=\"center\"><tbody><tr><td valign=\"top\" width=\"25%\">办公地址:</td><td valign=\"top\" width=\"75%\">广西贵港市金港大道XX号</td></tr><tr><td valign=\"top\" width=\"25%\">工厂地址:</td><td valign=\"top\">广西贵港市覃塘XX工业区</td></tr><tr><td valign=\"top\" width=\"25%\">联系电话:</td><td valign=\"top\">0775-4000001 　0775-<span>4000002</span> <br />0775-<span>4000003</span> </td></tr><tr><td valign=\"top\" width=\"25%\">传真号码:</td><td valign=\"top\">0775-<span>4000000</span></td></tr><tr><td valign=\"top\" width=\"25%\">手机号码:</td><td valign=\"top\">151xxxx100 陶 134xxxx228 莫</td></tr><tr><td valign=\"top\" width=\"25%\">销　　售:</td><td valign=\"top\">QQ:22xxxx88 QQ:22xxxx69<br />QQ:11xxxx08</td></tr><tr><td valign=\"top\" width=\"25%\">售　　后:</td><td valign=\"top\">QQ:12xxxx22</td></tr><tr><td valign=\"top\" width=\"25%\">邮　　箱:</td><td valign=\"top\">dzh188@qq.com </td></tr><tr><td valign=\"top\" width=\"25%\">网　　址:</td><td valign=\"top\">www.caozha.com</td></tr></tbody></table><p style=\"clear:both\"><br /></p>', '2012-08-11 15:59:56', 0);

